change_filament_gcode:
; change filament start
{if total_toolchanges == 0 and current_extruder == 1}
M104 S0 T0
{elsif total_toolchanges > 0 and current_extruder == 0}
M104 S{nozzle_temperature[0]}
{if layer_z == initial_layer_print_height}
T1
M109 S{nozzle_temperature_initial_layer[1]} T1
{else}
T1
M109 S{nozzle_temperature[1]} T1
{endif}
{elsif total_toolchanges > 0 and current_extruder == 1}
M104 S{nozzle_temperature[1]}
{if layer_z == initial_layer_print_height}
T0
M109 S{nozzle_temperature_initial_layer[0]} T0
{else}
T0
M109 S{nozzle_temperature[0]} T0
{endif}
{endif}
