change_filament_gcode:
;***** Update: 20230420
;----- layer_num: [layer_num]
;----- tool change: [toolchange_count]/[total_toolchanges]
{if next_extruder != current_extruder }
  {if total_toolchanges > 0}; if wipe tower is enabled
    M106 S0 ;fan off
    {if toolchange_count < total_toolchanges + 1}
      M104 S{temperature_vitrification[current_extruder]} ;standby T[current_extruder]
    {else}
      M104 S0 ;off T[current_extruder]
    {endif}
  {endif}
; <<< Current ToolHead T[current_extruder]

; Next ToolHead T[next_extruder] >>>
  T{next_extruder}
  M2000 S200 V250 A6000 ;quick switch extruders, S:200 mode/V:nominal speed/A:acceleration
  M109 S{if layer_num < 1}[nozzle_temperature_initial_layer]{else}[nozzle_temperature]{endif} C2 W1 ;wait T{next_extruder}
{endif}
